        #region Methods
        public void GetCompanyData()
        {
            try
            {
                if (sqlconn == null)
                {
                    sqlconn = new SqlConnection(ConnString);
                    sqlconn.Open();
                }
                else
                {
                    if (sqlconn.State == ConnectionState.Closed)
                        sqlconn.Open();
                }

                DataSet m_DsFinYear = new DataSet();

                string sqlstr = "Select * From Vudyog..Co_mast Where CompId=" + CompId;
                sqlstr = sqlstr + " " + "Select * From ManuFact";

                m_DsFinYear = cls_Sqlhelper.ExecuteDataset(sqlconn, CommandType.Text, sqlstr);
                m_DsFinYear.Tables[0].TableName = "Co_mast";
                m_DsFinYear.Tables[1].TableName = "ManuFact";

                DataRow drow;

                drow = m_DsFinYear.Tables["Co_mast"].Rows[0];

                Sta_dt = Convert.ToDateTime(drow["sta_dt"]).ToString("dd/MM/yyyy");
                End_dt = Convert.ToDateTime(drow["end_dt"]).ToString("dd/MM/yyyy");
                City = drow["city"].ToString().Trim();

                FinYear = Sta_dt.Substring(Sta_dt.Length - 4, 4) + "-" + End_dt.Substring(End_dt.Length - 4, 4);
                //Sta_dt = "04/01/2013";
                //End_dt = "12/31/2014";
                //FinYear = "2013-2014";
                FromDate = Sta_dt;
                ToDate = End_dt;

                //int[] month = { 4, 5, 6, 7, 8, 9 };
                //foreach (int m in month)
                //{
                //    if (Sta_dt.Substring(Sta_dt.Length - 7, 2) == m.ToString())
                //    {
                //        ReturnPeriod = "April-September";
                //        break;
                //    }
                //    else
                //    {
                //        ReturnPeriod = "October-March";
                //        break;
                //    }
                //}

                drow = m_DsFinYear.Tables["ManuFact"].Rows[0];
                STCNo = drow["sregn"].ToString().Trim();
                StrpId = drow["strpid"].ToString().Trim();
                StrpNm = drow["strpnm"].ToString().Trim();
                Signature = drow["signature"].ToString().Trim();
            }
            catch (Exception ex)
            {
                throw ex;
                //CallMessageBox.Show(ex.Message, Vumess);
                //return;
            }
            finally
            {
                sqlconn.Close();
            }
        }

        public void GetProcData()
        {
            try
            {
                BgWorkProgress.ReportProgress(2, "Opening DataBase Connection....");

                if (sqlconn == null)
                {
                    sqlconn = new SqlConnection(ConnString);
                    sqlconn.Open();
                }
                else
                {
                    if (sqlconn.State == ConnectionState.Closed)
                        sqlconn.Open();
                }

                DataSet m_DsSelect = new DataSet();

                BgWorkProgress.ReportProgress(3, "Executing SQL Procedure....");

                string sqlstr = string.Empty;
                //sqlstr = "EXECUTE Usp_Rep_ST3_AccrualBasis '','','','" + FromDate + "','" + ToDate + "','','','','',0,0,'','','','','','','','','" + FinYear + "',''";
                sqlstr = "EXECUTE Usp_Rep_ST3_AccrualBasis_OCT13 '','','','" + FromDate + "','" + ToDate + "','','','','',0,0,'','','','','','','','','" + FinYear + "',''";

                m_DsSelect = cls_Sqlhelper.ExecuteDataset(ConnString, CommandType.Text, sqlstr);
                BgWorkProgress.ReportProgress(5, "Execution Completed....");

                DsMain = m_DsSelect;
                m_DsSelect.Dispose();
            }
            catch (Exception ex)
            {
                throw ex;
                //CallMessageBox.Show(ex.Message, Vumess);
                //return;
            }
            finally
            {
                sqlconn.Close();
            }
        }

        public void GetDate()
        {
            try
            {
                switch (ConstitutionType)
                {
                    case "PROPRIETORSHIP/INDIVIDUAL": //Quarterly
                    case "LIMITED LIABILITY PARTNERHIP": //Quarterly
                    case "A FIRM": //Quarterly
                        //FDate = DateTimeFormatInfo.CurrentInfo.GetMonthName(Convert.ToInt32(d1[1]));

                        MonthlyQuarterly = "QUARTERLY";
                        break;

                    case "REGISTERED PUBLIC LTD COMPANY": //Monthly
                    case "REGISTERED PRIVATE LTD COMPANY": //Monthly
                    case "REGISTERED TRUST": //Monthly
                    case "SOCIETY/CO-OP SOCIETY": //Monthly
                    case "HINDU UNDIVIDED FAMILY": //Monthly
                    case "GOVERNMENT": //Monthly
                    case "A LOCAL AUTHORITY": //Monthly
                    case "AN ASSOCIATION OF PERSONS OR BODY OF INDIVIDUALS, WHETHER INCORPORATED OR NOT": //Monthly
                    case "EVERY ARTIFICIAL JURIDICAL PERSON, NOT FALLING WITHIN ANY OF THE PRECEDING SUB-CLAUSES": //Monthly

                        MonthlyQuarterly = "MONTHLY";
                        break;
                }
            }
            catch (Exception ex)
            {
                throw ex;
                //CallMessageBox.Show(ex.Message, Vumess);
                //return;
            }
        }

